mapper.select:
SELECT ranked.id, ranked.customerId, ranked.nickName, ranked.socialPlatform, ranked.birthday FROM ( SELECT ccc.id, ccc.customer_id AS customerId, ccc.nick_name AS nickName, ccc.social_platform AS socialPlatform, ccc.birthday, @row_number := CASE WHEN @current_customer_id = ccc.customer_id THEN @row_number + 1 ELSE 1 END AS row_num, @current_customer_id := ccc.customer_id FROM customer_customer_contact ccc CROSS JOIN (SELECT @row_number := 0, @current_customer_id := NULL) AS vars WHERE ccc.del_flag = '0' AND ccc.primary_contact_flag = '1' AND ccc.customer_id IN
#{customerId}
ORDER BY ccc.customer_id, ccc.create_time DESC ) AS ranked WHERE row_num = 1;

mapper.update:
update customer_customer_contact
customer_id = #{customerId}, nick_name = #{nickName}, email = #{email}, social_platform = #{socialPlatform}, phone = #{phone}, rank = #{rank}, position = #{position}, birthday = #{birthday}, sex = #{sex}, avatar_or_business_card = #{avatarOrBusinessCard}, contact_remarks = #{contactRemarks}, primary_contact_flag = #{primaryContactFlag}, order_index = #{orderIndex}, del_flag = #{delFlag}, create_id = #{createId}, create_by = #{createBy}, create_time = #{createTime}, update_id = #{updateId}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
