mapper.select:
SELECT COUNT(DISTINCT (c.id)) FROM customer_customer c LEFT JOIN customer_customer_segment cs ON c.id = cs.customer_id LEFT JOIN customer_customer_follow_up_personnel fup ON c.id = fup.customer_id AND fup.del_flag = '0' WHERE c.sea_type = 1 AND c.del_flag = '0' AND fup.user_id in
#{userId}
AND ( #{segmentId} = -2 OR (#{segmentId} = -1 AND c.focus_flag = 1) OR (#{segmentId} > 0 AND cs.segment_id = #{segmentId}) )

SELECT customer.id, customer.company_name as companyName, (SELECT GROUP_CONCAT( CONCAT(t.id, '_', t.name, '_', t.color) ) FROM customer_customer_tag ct LEFT JOIN customer_tag t ON ct.tag_id = t.id WHERE ct.customer_id = customer.id AND t.del_flag = '0') AS tagStr, customer.packet_id as packetId, packet.name as packetName, stage.id as stageId, stage.name as stageName, (select nick_name from customer_customer_contact contact where contact.customer_id = customer.id and contact.del_flag = '0' and contact.primary_contact_flag = 1 order by contact.create_time desc limit 1) as primaryContact, customer.last_contacted_at as lastContactedAt, customer.country_region as countryRegion, customer.create_time as createTime, customer.customer_no as customerNo, customer.short_name as shortName, customer.focus_flag as focusFlag FROM customer_customer customer LEFT JOIN customer_packet packet ON customer.packet_id = packet.id AND packet.del_flag = '0' LEFT JOIN customer_stage stage ON customer.stage_id = stage.id AND stage.del_flag = '0' LEFT JOIN customer_customer_segment customerSegment ON customer.id = customerSegment.customer_id LEFT JOIN customer_customer_follow_up_personnel fup ON customer.id = fup.customer_id AND fup.del_flag = '0' WHERE customer.sea_type = 1 AND customer.del_flag = '0' AND fup.user_id IN
#{userId}
AND ( #{segmentId} = -2 OR (#{segmentId} = -1 AND customer.focus_flag = 1) OR (#{segmentId} > 0 AND customerSegment.segment_id = #{segmentId}) ) GROUP BY customer.id ORDER BY customer.create_time DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM customer_customer customer WHERE customer.sea_type = 2 AND customer.del_flag = '0'
AND customer.publicleads_groups_id = #{publicleadsGroupsId}
AND customer.packet_id = #{packetId}

SELECT customer.id, customer.company_name as companyName, customer.short_name as shortName, (SELECT GROUP_CONCAT( CONCAT(t.id, '_', t.name) ) FROM customer_customer_tag ct LEFT JOIN customer_tag t ON ct.tag_id = t.id WHERE ct.customer_id = customer.id AND t.del_flag = '0') AS tagStr, customer.packet_id as packetId, packet.name as packetName, stage.id as stageId, stage.name as stageName, (select concat(u.nick_name) from customer_customer_follow_up_personnel fup left join sys_user u on fup.user_id = u.user_id where fup.customer_id = customer.id and fup.del_flag = '0' order by fup.create_time desc) as followUpPersonnel, customer.country_region as countryRegion, customer.last_contacted_at as lastContactedAt, customer.timezone as timezone, customer.focus_flag as focusFlag FROM customer_customer customer LEFT JOIN customer_packet packet ON customer.packet_id = packet.id AND packet.del_flag = '0' LEFT JOIN customer_stage stage ON customer.stage_id = stage.id AND stage.del_flag = '0' WHERE customer.sea_type = 2 AND customer.del_flag = '0'
AND customer.publicleads_groups_id = #{publicleadsGroupsId}
AND customer.packet_id = #{packetId}
GROUP BY customer.id ORDER BY customer.create_time DESC LIMIT #{offset}, #{limit}

select count(distinct (c.id)) from customer_customer c
left join customer_customer_follow_up_personnel fup on c.id = fup.customer_id and fup.del_flag = '0'
where c.del_flag = '0' and c.sea_type = 1 and fup.user_id = #{userId}

select count(distinct(cc.id)) from customer_customer cc left join customer_customer_contact ccc on cc.id = ccc.customer_id and ccc.del_flag = '0' where cc.del_flag = '0'
and (cc.company_name like CONCAT('%', #{searchText}, '%') or cc.short_name like CONCAT('%', #{searchText}, '%') or cc.customer_no = #{searchText} or ccc.email like concat('%', #{searchText}) or ccc.nick_name = #{searchText} or ccc.phone like concat('%', #{searchText}, '%') or ccc.social_platform like concat('%', #{searchText}, '%'))
and (cc.company_name like CONCAT('%', #{searchText}, '%') or cc.short_name like CONCAT('%', #{searchText}, '%'))
and cc.customer_no = #{searchText}
and ccc.email = #{searchText}
and ccc.email like concat('%', #{searchText})
and ccc.nick_name = #{searchText}
and ccc.phone like CONCAT('%', #{searchText}, '%')
and ccc.social_platform like CONCAT('%', #{searchText}, '%')

SELECT cc.id, cc.customer_no AS customerNo, cc.company_name AS companyName, cc.short_name AS shortName, cc.country_region AS countryRegion, cc.create_time AS createTime, cs.name as customerStageName, ccc.nick_name AS contactNickName, ccc.email AS customerEmail, ccc.phone AS customerPhone, ccc.social_platform AS contactSocialPlatform, GROUP_CONCAT(cs2.name) AS customerSourceName FROM customer_customer cc LEFT JOIN customer_customer_contact ccc ON cc.id = ccc.customer_id AND ccc.del_flag = '0' LEFT JOIN customer_stage cs ON cc.stage_id = cs.id AND cs.del_flag = '0' LEFT JOIN customer_customer_source ccs ON cc.id = ccs.customer_id LEFT JOIN customer_source cs2 ON ccs.source_id = cs2.id AND cs2.del_flag = '0' WHERE cc.del_flag = '0'
AND (cc.company_name LIKE CONCAT('%', #{searchText}, '%') OR cc.short_name LIKE CONCAT('%', #{searchText}, '%') OR cc.customer_no = #{searchText} OR ccc.email LIKE CONCAT('%', #{searchText}, '%') OR ccc.nick_name = #{searchText} OR ccc.phone LIKE CONCAT('%', #{searchText}, '%') OR ccc.social_platform LIKE CONCAT('%', #{searchText}, '%'))
AND (cc.company_name LIKE CONCAT('%', #{searchText}, '%') OR cc.short_name LIKE CONCAT('%', #{searchText}, '%'))
AND cc.customer_no = #{searchText}
AND ccc.email = #{searchText}
AND ccc.email LIKE CONCAT('%', #{searchText}, '%')
AND ccc.nick_name = #{searchText}
AND ccc.phone LIKE CONCAT('%', #{searchText}, '%')
AND ccc.social_platform LIKE CONCAT('%', #{searchText}, '%')
AND ccc.id = ( SELECT ccc_sub.id FROM customer_customer_contact ccc_sub WHERE ccc_sub.customer_id = cc.id AND ccc_sub.del_flag = '0' ORDER BY ccc_sub.create_time DESC LIMIT 1 ) GROUP BY cc.id ORDER BY cc.create_time DESC LIMIT #{offset}, #{limit};

select user_id as userId, nick_name as nickName, dept_id as deptId from sys_user where dept_id in

    #{deptId}

and status = 0 and del_flag = '0'

SELECT MAX(CAST(SUBSTRING(CONVERT(customer_no, SIGNED), 1, LENGTH(customer_no)) AS UNSIGNED))
FROM customer_customer
WHERE customer_no REGEXP '^[0-9]+$';

mapper.update:
update customer_customer
company_website = #{companyWebsite}, company_name = #{companyName}, short_name = #{shortName}, country_region = #{countryRegion}, sea_type = #{seaType}, publicleads_groups_id = #{publicleadsGroupsId}, packet_id = #{packetId}, stage_id = #{stageId}, rating = #{rating}, customer_no_type = #{customerNoType}, customer_no = #{customerNo}, phone_prefix = #{phonePrefix}, phone = #{phone}, timezone = #{timezone}, scale = #{scale}, fax = #{fax}, address = #{address}, company_remarks = #{companyRemarks}, company_logo = #{companyLogo}, focus_flag = #{focusFlag}, last_contacted_at = #{lastContactedAt}, last_followup_at = #{lastFollowupAt}, del_flag = #{delFlag}, create_id = #{createId}, create_by = #{createBy}, create_time = #{createTime}, update_id = #{updateId}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

update customer_customer set packet_id = #{packetId} where id in
#{id}
and del_flag = '0'

update customer_customer set sea_type = #{seaType}

    id in

        #{id}
